import React, { Component } from 'react';
import './App.css';
import Filter from './components/Filter';
import Count from './components/Count';
import Movies from './components/Movies';
import Limit from './components/Limit';

class App extends Component {
  constructor(){
  super();
  this.state = {
    movies:       [],
    showFilter:   false,
    limit:        40,
    imdb:         undefined,
    year:         undefined,
    star_rating:  undefined,
    star_count:   undefined,
    genre:        undefined
  };

  this.handleChange = this.handleChange.bind(this);
  this.handleSubmit = this.handleSubmit.bind(this);
}

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  handleChange = (key, value) => {
    this.setState({ [key] : value });
  }

  toggleFilter = () => {
    this.setState({ showFilter: !this.state.showFilter });
  }

  showMore = () => {
    this.setState( state => {
      ({ limit: this.state.limit + 40 })
      console.log(this.state.limit)
    });


    //this.handleSubmit();
  }

  handleSubmit = () => {
    const params = {
      imdb:         this.state.imdb,
      year:         this.state.year,
      star_rating:  this.state.star_rating,
      star_count:   this.state.star_count,
      genre:        this.state.genre,
      limit:        this.state.limit,
    };
    console.log('limit',this.state.limit)
    let query = `/users/filter?`;
    query += Object.keys(params)
      .filter(a => typeof params[a] !== 'undefined')
      .map(k => `${k}=${params[k]}`)
      .join('&');
    console.log(query);
    fetch(query)
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  render() {
    return (
      <div className="App">
        <h1>Amazon Prime Movies</h1>
        <button className='toggle_filter' onClick={this.toggleFilter}>Filter</button>
        {this.state.showFilter && <Filter
          filterMovies = { this.filterMovies }
          updateParams = { this.handleChange }
          handleSubmit = { this.handleSubmit }
          starRating = { this.state.star_rating }
          showFilter = { this.state.showFilter }
          imdb = { this.state.imdb }
        />}
        <Count moviesLength = { this.state.movies.length } />
        <Movies movies = { this.state.movies } />
        <Limit showMore = { this.showMore } />
      </div>
    );
  };
};

export default App;
