import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Filter from './components/Filter';
import Movies from './components/Movies';

class App extends Component {
  constructor(props) {
    super(props);
    this.state= {
      movies: [],
      imdb: undefined,
      year: undefined,
      star_rating: undefined,
      star_count: undefined
    };

  }

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  filterMovies = (params) => {
    console.log('params', params)
    this.setState({
      imdb: params.imdb,
      year: params.year,
      star_rating: params.star_rating,
      star_count: params.star_count
     })
    // const IMDB = this.state.imdb;
    // const year = this.state.year;
    // const star_rating = this.state.star_rating
    // const star_count = this.state.star_count
    let query = `/users/filter?`;
    if (this.state.imdb !== undefined) query += `imdb=${this.state.imdb}`
    if (this.state.year !== undefined) query += `year=${this.state.year}`
    if (this.state.star_rating !== undefined) query += `star_rating=${this.state.star_rating}`
    if (this.state.star_count !== undefined) query += `star_count=${this.state.star_count}`
    console.log(query)
    fetch(query)
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  render() {
    return (
      <div className="App">
        <h1>Movies</h1>
        <Filter
          filterMovies = { this.filterMovies}
          moviesLength = { this.state.movies.length }
        />

        <Movies
          movies = { this.state.movies }
        />

      </div>
    );
  };
};

export default App;
