import React, { Component } from 'react';
const date = new Date();
const year = date.getFullYear();


class Filter extends Component {
  constructor(props) {
    super(props);
    this.state= {
      imdb: undefined,
      year: undefined,
      star_rating: undefined,
      star_count: undefined
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (e) => {
    this.setState({ [e.target.name] : e.target.value })
  }

  handleSubmit = (e) => {

  }

  render(){
    return(
      <div className="filter">
        <form onSubmit={this.handleSubmit}>
          <span> IMDB Rating: </span>
          <input
            className="form__input slider"
            type="range" min="1" value={this.imdb} max="10"
            onChange={this.handleChange}
            name="imdb"
          />
          <span> Amazon Star Rating: </span>
          <input
           className="form__input slider"
           type="range" min="1" defaultValue="3" max="5"
           name="star_rating"
          />
          <span> Number of Amazon Reviews: </span>
          <input
            className="form__input"
            type="number" min="1"
            name="star_count"
          />
          <span> Year: </span>
          <input
            className="form__input year"
            type="number" min="1950" max={year}
            name="year"
          />
          <button className="form__button">Search</button>
        </form>
        <br/>
        <span>Displaying {this.props.moviesLength} movies</span>
      </div>
    )
  }
}

export default Filter;
