import React, { Component } from 'react';

class Number extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: undefined
    }
    this.handleChange = this.handleChange.bind(this);
  }

  isYear = (val) => {
    const min = 1916;
    if (val.length !== 4) return this.setState({ error: 'Please a 4 Digit Year'})
    if (val < min) return this.setState({ error: 'Please Enter a Year After 1916'}) 
  }

  isNumeric = (val) => {
    const re = /^\d+$/;
    return re.test(val);
  }

  handleChange = (e) => {
    if (this.isNumeric(e.target.value) || e.target.value === '') {
      this.setState({ error: undefined })
      this.props.handleChange(e);
    } else {
      if (e.target.name === 'star_count') {
        this.setState({ error: 'Please Enter a Number' })
      } else if (e.target.name === 'year') {
        this.setState({ error: 'Please Enter a valid date' })
      }
    }
  }

  render() {
    return(
      <div className="number">
        <input
          type="text"
          placeholder = {this.props.placeholder}
          value = { this.props.value }
          onChange={this.handleChange}
          name={this.props.name}
        />
      <label htmlFor={this.props.name}>{this.props.label}</label>
      {this.state.error &&
      <span className='error'>{this.state.error}</span>}
      </div>
    )
  }
}

export default Number;
