import React, { Component } from 'react';
import Range from './inputs/Range';
import Number from './inputs/Number';

const date = new Date();
const year = date.getFullYear();


class Filter extends Component {
  constructor(props) {
    super(props);
    this.state= {
      imdb: undefined,
      year: undefined,
      star_rating: undefined,
      star_count: undefined
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (e) => {
    this.props.updateParams( e.target.name, e.target.value );
  }

  handleSubmit = (e) => {
    e.preventDefault()
    this.props.filterMovies();
  }

  render(){
    return(
        <form onSubmit = {this.handleSubmit}>
          <Range
           label = 'IMDB' name = 'imdb' max = '10'
           handleChange = { this.handleChange }
          />
          <Range
            label = 'Amazon stars' name = 'star_rating' max = '5'
            handleChange = {this.handleChange }
          />
          <Number
           label = 'Number of Amazon Reviews' name = 'star_count'
           handleChange = {this.handleChange }
          />
          <Number
            label = 'Year' name = 'year'
            handleChange = {this.handleChange }
          />
          <button className="form__button">Search</button>
        </form>
    )
  }
}

export default Filter;
