import React, { Component } from 'react';
import Transition from 'react-transition-group/Transition';
import Range from './inputs/Range';
import Number from './inputs/Number';
import Select from './inputs/Select';

const date = new Date();
const year = date.getFullYear();

class Filter extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = (e) => {
    console.log(e.target.name, e.target.value)
    this.props.updateParams( e.target.name, e.target.value );
  }

  render(){
    return(
      <Transition timeout={500} in={this.props.showFilter}>
        {(state) => (
          <form onSubmit = {this.props.handleSubmit} className='animate_filter'>
            <Range
             label = 'IMDB' name = 'imdb' max = '10'
             value = { this.props.imdb }
             handleChange = { this.handleChange }
            />
            <Range
              label = 'Amazon stars' name = 'star_rating' step = '0.5' max = '5'
              value = { this.props.starRating }
              handleChange = {this.handleChange }
            />
              <Number
               label = 'Number of Amazon Reviews' name = 'star_count'
               handleChange = {this.handleChange }
              />
              <Select
                handleChange = {this.handleChange }
              />
              <Number
                label = 'Year' name = 'year'
                handleChange = {this.handleChange }
              />
            <button>Search</button>
          </form>
        )}
      </Transition>
    )
  }
}

export default Filter;
