import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Filter from './components/Filter';
import Count from './components/Count';
import Movies from './components/Movies';

class App extends Component {
  constructor(){
  super();
  this.state = {
    movies: [],
    imdb: undefined,
    year: undefined,
    star_rating: undefined,
    star_count: undefined
  };

  this.handleChange = this.handleChange.bind(this);
  this.handleSubmit = this.handleSubmit.bind(this);
}

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  handleChange = (key, value) => {
    this.setState({ [key] : value })
  }

  handleSubmit = (e) => {
    console.log('main state', this.state)
    let query = `/users/filter?`;
    if (this.state.imdb !== undefined) query += `imdb=${this.state.imdb}`
    if (this.state.year !== undefined) query += `year=${this.state.year}`
    if (this.state.star_rating !== undefined) query += `star_rating=${this.state.star_rating}`
    if (this.state.star_count !== undefined) query += `star_count=${this.state.star_count}`
    console.log(query)
    fetch(query)
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  render() {
    return (
      <div className="App">
        <h1>Movies</h1>
        <Filter
          filterMovies = { this.filterMovies }
          updateParams = { this.handleChange }
          handleSubmit = { this.handleSubmit }
          imdb = { this.state.imdb }
          starRating = {this.state.star_rating}
        />
        <Count moviesLength = { this.state.movies.length } />
        <Movies movies = { this.state.movies } />
      </div>
    );
  };
};

export default App;
