import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Filter from './components/Filter';
import Movies from './components/Movies';

class App extends Component {
  state= {
    movies: [],
    imdb: undefined
  };

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  updateFilter = (e) => {
    this.setState({ [e.target.name] : e.target.value })
  }

  getMovies = (e) => {
    e.preventDefault();
    const IMDB = e.target.elements.imdb.value
    const year = e.target.elements.year.value
    const star_rating = e.target.elements.star_rating.value
    const star_count = e.target.elements.star_count.value
    const query_prefix = `/users/filter?`;
    fetch(`/users/filter?imdb=${IMDB}&year=${year}&star_rating=${star_rating}&star_count=${star_count}`)
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  render() {
    return (
      <div className="App">
        <h1>Movies</h1>
        <Filter
          getMovies = { this.getMovies }
          moviesLength = { this.state.movies.length }
          updateFilter = { this.updateFilter }

        />
        <Movies  movies = { this.state.movies } />
      </div>
    );
  };
};

export default App;
