import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Filter from './components/Filter';
import Movies from './components/Movies';

class App extends Component {
  constructor(props) {
    super(props);
    this.state= {
      movies: [],
      imdb: undefined,
      year: undefined,
      star_rating: undefined,
      star_count: undefined
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  handleChange = (e) => {
    this.setState({ [e.target.name] : e.target.value })
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const IMDB = this.state.imdb;
    const year = this.state.year
    const star_rating = this.state.star_rating
    const star_count = this.state.star_count
    let query = `/users/filter?`;
    if (IMDB !== undefined) query += `imdb=${IMDB}`
    if (year !== undefined) query += `year=${year}`
    if (star_rating !== undefined) query += `star_rating=${star_rating}`
    if (star_count !== undefined) query += `star_count=${star_count}`
    console.log(query)
    fetch(query)
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  };

  render() {
    return (
      <div className="App">
        <h1>Movies</h1>
        <Filter
          handleSubmit = { this.handleSubmit }
          handleChange = { this.handleChange }
          moviesLength = { this.state.movies.length }
          imdb = { this.state.imdb }
        />

        <Movies
          movies = { this.state.movies }
        />

      </div>
    );
  };
};

export default App;
