import React, { Component } from 'react';
import Range from './inputs/Range';
import Number from './inputs/Number';

const date = new Date();
const year = date.getFullYear();


class Filter extends Component {
  constructor(props) {
    super(props);
    this.state= {
      imdb: undefined,
      year: undefined,
      star_rating: undefined,
      star_count: undefined
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (e) => {
    this.setState({ [e.target.name] : e.target.value })
  }

  handleSubmit = (e) => {
    e.preventDefault()
    console.log('filter state imdb', this.state.imdb)
    this.props.filterMovies(this.state.imdb);
  }

  render(){
    return(
      <div className='filter'>
        <form>
          <Range lable='IMDB' name='imdb' handleChange = { this.handleChange }/>
          <Range label='Amazon stars' name='star_rating' handleChange = { this.handleChange }/>
          <Number name = '' />
          <span> Number of Amazon Reviews: </span>
          <input
            className="form__input"
            type="number" min="1"
            name="star_count"
          />
          <span> Year: </span>
          <input
            className="form__input year"
            type="number" min="1950" max={year}
            name="year"
          />
          <button className="form__button">Search</button>
        </form>
        <br/>
        <span>Displaying {this.props.moviesLength} movies</span>
      </div>
    )
  }
}

export default Filter;
