import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state= {
    movies: []
  }

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(result => this.setState({ movies: result.rows }));
  }

  render() {
    return (
      <div className="App">
        <h1>Movies</h1>
          {this.state.movies.map(movie => {
            return (
              <div className="col-md-4" key={ movie.id }>
                <div className="movies__box">
                  <img
                    className="movie__box-img"
                    src={ movie.picture }
                    alt={ movie.title }
                  />
                  <div className="movie__text">
                    <h5 className="movies__title">
                      { movie.title }
                    </h5>
                    <p className="movies__subtitle">IMDB Rating: <span>
                        { movie.imdb }
                      </span>
                    </p>
                  </div>
                    <a
                      className="movie_buttons"
                      target="_blank"
                      href={ movie.url }
                    >View Movie</a>
                </div>
              </div>
            );
          })
        }
      </div>
    );
  }
}

export default App;
